import React from 'react'
import TodoForm from './TodoForm'
import Todo from './Todo'

export default function Category({todo, index, addTodo, deleteCategory}) {


    const mapOverTodos = () => {
           return todo.todos.map((singleTodo) => {
                return  <Todo singleTodo={singleTodo} deleteTodo={deleteTodo} />
            }) 
    }

    const handleClick = e => {
        deleteCategory(todo.id)
    }

    return (
        <div>
            <h4>{todo.name}</h4>
            <div onClick={handleClick}><button>x</button></div>
            {mapOverTodos()}
            <div>
                <TodoForm category={todo.id}  addTodo={addTodo}/>
            </div>
        </div>
    )
}
