import React from 'react'
import TodoForm from './TodoForm'

export default function Todo({todo, index, addTodo, deleteTodo}) {

    const handleClick = (e, todo)=> {
        deleteTodo(todo.id)
    }

    return (
        <div>
            <h4>{todo.name}</h4>
            { todo.todos.map(todo => {
               return (
               <div>
                   <div><p>{todo.text}</p></div>
                   <div onClick={handleClick(todo.id)}><button>x</button></div>
                </div>
                   )
            }) }
            <div>
                <TodoForm category={todo.id}  addTodo={addTodo}/>
            </div>
        </div>
    )
}
