import React, { useState, useEffect } from 'react';
import Category from './Category';
import TodoCategoryForm from './TodoCategoryForm';
import { FontAwesomeIcon } from '@fortawesome/react-fontawesome';


const Home = () => {

const [hasError, setErrors] = useState(false);
const [ todos, setTodos ] = useState([]);

useEffect(() => {
      fetch("http://localhost:3000/categories")
        .then( res =>  res.json())
        .then(data => {
            setTodos(data)
        })  
        .catch(err => setErrors(err));
}, [])


const addCategory = name => {
    let config = {
      method: "POST",
      headers: {
        'Content-Type':'application/json',
        'Accept':'application/json'
      },
      body: JSON.stringify(name)
    }
    fetch("http://localhost:3000/categories", config)
      .then(rsp => rsp.json())
      .then(data => {

         setTodos(data)
      })
}

const addTodo = data => {
  let config = {
    method: "POST",
    headers: {
      'Content-Type':'application/json',
      'Accept':'application/json'
    },
    body: JSON.stringify(data)
  }
  fetch("http://localhost:3000/todos", config)
    .then(rsp => rsp.json())
    .then(data => {
       setTodos(data)
    })
}

const deleteTodo = todo => {
  let config = {
    method: "DELETE",
    headers: {
      'Content-Type':'application/json',
      'Accept':'application/json'
    }
  }

  fetch(`http://localhost:3000/todos/${todo}`, config)
    .then(rsp => rsp.json())
    .then(data => {
       setTodos(data)
    })
}

const deleteCategory = category => {
  let config = {
    method: "DELETE",
    headers: {
      'Content-Type':'application/json',
      'Accept':'application/json'
    }
  }

  fetch(`http://localhost:3000/categories/${category}`, config)
    .then(rsp => rsp.json())
    .then(data => {
       setTodos(data)
    })
}


const mapOverTodos = () => {
  if (todos.length > -1) {
    return (
           todos.map((todo, index) => (
              <Category key={index} index={index} todo={todo} addTodo={addTodo} deleteTodo={deleteTodo} deleteCategory={deleteCategory} />
          )) 

    )
  } 
}



  return (
    <div className="home">
          <div className="header">
            <div className="header__logo-menu-wrapper">
              <h1 className="header__logo-box">
                <span className="header__logo-span">ToDo List</span>
                <span className="header__sub-span">Get Organized</span>
              </h1>
              <div className="header__menu">
                <span>Login</span>
                <span>SignUp</span>
                <span>SignOut</span>
              </div>
            </div>
          </div>  
          <div className="home__create-category-box">
              <button type="button" className="home__create-category-button"><FontAwesomeIcon  icon="plus"></FontAwesomeIcon></button> 
              <TodoCategoryForm  addCategory={addCategory} />
          </div>
          <div className="categories">
              {mapOverTodos()}
          </div>
          <footer id="footer">
              <p>yonedesign</p>
          </footer>
    </div>
  );
}

export default Home;
