import React from 'react'
import TodoForm from './TodoForm'
import Todo from "../components/Todo"

export default function Category({todo, addTodo, deleteTodo}) {


    const mapOverTodos = () => {
        if (todo.length > -1) {
            todo.todos.map(todo => {
                return (
                    <div>
                        <Todo todo={todo} deleteTodo={deleteTodo} />
                    </div>
                )
            }) 
        }
    }

    return (
        <div>
            <h4>{todo.name}</h4>
            {mapOverTodos()}
            <div>
                <TodoForm category={todo.id}  addTodo={addTodo}/>
            </div>
        </div>
    )
}
