import React, { Fragment } from 'react'
import TodoForm from './TodoForm'
import Todo from './Todo'

export default function Category({todo, index, addTodo, deleteTodo, deleteCategory}) {


    const mapOverTodos = () => {
           return todo.todos.map((singleTodo) => {
                return  <Todo singleTodo={singleTodo} deleteTodo={deleteTodo} />
            }) 
    }

    const handleClick = e => {
        deleteCategory(todo.id)
    }

    return (
        <Fragment>
            <div className="category">
                <div className="category__header">
                    <div className="category__name">
                        <h4>{todo.name}</h4>
                    </div>
                    <div className="category__delete" onClick={handleClick}>
                        <button>x</button>
                    </div>
                    <div>
                    <img src='data:image/svg+xml;base64,PHN2ZyB4bWxucz0iaHR0cDovL3d3dy53My5vcmcvMjAwMC9zdmciIHdpZHRoPSIzMiIgaGVpZ2h0PSIzMiIgdmlld0JveD0iMCAwIDMyIDMyIj48cGF0aCBkPSJNMTYuMDAzIDE4LjYyNmw3LjA4MS03LjA4MUwyNSAxMy40NmwtOC45OTcgOC45OTgtOS4wMDMtOSAxLjkxNy0xLjkxNnoiLz48L3N2Zz4='/>
                    </div>
                </div>
                <div className="todo">
                    {mapOverTodos()}
                </div>
                <div className="category__new-form">
                    <TodoForm category={todo.id}  addTodo={addTodo}/>
                </div>
            </div>
        </Fragment>
    )
}
